use std::io::{self, BufRead, Write};

fn main() {
    let stdin = io::stdin();
    let mut stdout = io::stdout();
    let mut buffer = String::new();

    // Read the number of nodes
    stdin.lock().read_line(&mut buffer).unwrap();
    let n: usize = buffer.trim().parse().unwrap();
    buffer.clear();

    // Read the parent array
    let mut parent = vec![0; n];
    for i in 0..n {
        stdin.lock().read_line(&mut buffer).unwrap();
        parent[i] = buffer.trim().parse().unwrap();
        buffer.clear();
    }

    // Read the string s
    stdin.lock().read_line(&mut buffer).unwrap();
    let s = buffer.trim().to_string();
    buffer.clear();

    // Create a solution instance and find the answer
    let solution = Solution {};
    let ans = solution.find_answer(parent, s);

    // Print the result
    for b in ans {
        if b {
            writeln!(&mut stdout, "true ").unwrap();
        } else {
            writeln!(&mut stdout, "false ").unwrap();
        }
    }
}

